// Package html contains functionality to render output to an html file
package html

import (
	"fmt"
	"log"
	"math"
	"path/filepath"
	"strconv"
	"strings"
	"text/template"
	"time"

	"github.com/devops-kung-fu/common/util"
	"github.com/gomarkdown/markdown"
	"github.com/microcosm-cc/bluemonday"
	"github.com/spf13/afero"

	"github.com/devops-kung-fu/bomber/models"
)

// Renderer contains methods to render results to an HTML file
type Renderer struct{}

// Render renders results to an HTML file
func (Renderer) Render(results models.Results) error {
	var afs *afero.Afero

	if results.Meta.Provider == "test" {
		afs = &afero.Afero{Fs: afero.NewMemMapFs()}
	} else {
		afs = &afero.Afero{Fs: afero.NewOsFs()}
	}

	filename := generateFilename()
	util.PrintInfo("Writing filename:", filename)

	err := writeTemplate(afs, filename, results)
	if err != nil {
		log.Println(err)
	}
	return err
}

// generateFilename generates a unique filename based on the current timestamp
// in the format "2006-01-02 15:04:05" and replaces certain characters to
// create a valid filename. The resulting filename is a combination of the
// timestamp and a fixed suffix.
func generateFilename() string {
	t := time.Now()
	r := strings.NewReplacer("-", "", " ", "-", ":", "-")
	return filepath.Join(".", fmt.Sprintf("%s-bomber-results.html", r.Replace(t.Format("2006-01-02 15:04:05"))))
}

// writeTemplate writes the results to a file with the specified filename,
// using the given Afero filesystem interface. It creates the file, processes
// percentiles in the results, converts Markdown to HTML, and writes the
// templated results to the file. It also sets file permissions to 0777.
func writeTemplate(afs *afero.Afero, filename string, results models.Results) error {
	processPercentiles(results)

	file, err := afs.Create(filename)
	if err != nil {
		log.Println(err)
		return err
	}

	markdownToHTML(results)

	template := genTemplate("output")
	err = template.ExecuteTemplate(file, "output", results)
	if err != nil {
		log.Println(err)
		return err
	}

	err = afs.Fs.Chmod(filename, 0777)
	if err != nil {
		log.Println(err)
		return err
	}

	return nil
}

// processPercentiles calculates and updates the percentile values for
// vulnerabilities in the given results. It converts the percentile from
// a decimal to a percentage and updates the results in place.
func processPercentiles(results models.Results) {
	for i, p := range results.Packages {
		for vi, v := range p.Vulnerabilities {
			per, err := strconv.ParseFloat(v.Epss.Percentile, 64)
			if err != nil {
				log.Println(err)
			} else {
				percentage := math.Round(per * 100)
				if percentage > 0 {
					results.Packages[i].Vulnerabilities[vi].Epss.Percentile = fmt.Sprintf("%d%%", uint64(percentage))
				} else {
					results.Packages[i].Vulnerabilities[vi].Epss.Percentile = "N/A"
				}
			}
		}
	}
}

// markdownToHTML converts the Markdown descriptions of vulnerabilities in
// the given results to HTML. It uses the Blackfriday library to perform the
// conversion and sanitizes the HTML using Bluemonday.
func markdownToHTML(results models.Results) {
	for i := range results.Packages {
		for ii := range results.Packages[i].Vulnerabilities {
			md := []byte(results.Packages[i].Vulnerabilities[ii].Description)
			html := markdown.ToHTML(md, nil, nil)
			results.Packages[i].Vulnerabilities[ii].Description = string(bluemonday.UGCPolicy().SanitizeBytes(html))
		}
	}
}

func genTemplate(output string) (t *template.Template) {

	content := `

<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>bomber Results</title>

	<style>
	body {
		font-family: Helvetica;
		margin: 20px;
	}
	#vuln {
		border: 1px solid;
		border-color: gray;
		border-radius: 5px;
		padding: 10px;
		margin-bottom: 10px;
	}
	#bomber-logo {
		margin-top: 10px;
		margin-bottom: 30px;
		width:331px;
		height:80px;
		background-image:url(data:image/png;base64,);
	}
	#summary {
		border: 0px;
	}
	#CRITICAL {
		color: red;
		font-weight: bold;
	}
	#HIGH {
		color: purple;
		font-weight: bold;
	}
	#MODERATE {
		color: goldenrod;
		font-weight: bold;
	}
	#LOW {
		color: green;
		font-weight: bold;
	}
	#UNSPECIFIED {
		color: blue;
		font-weight: bold;
	}
	h3 {
		font-weight: bold
	}
	#footer {
		width: 75px;
		height: 75px;
		margin-bottom: 10px;
		background-image:url(data:image/png;base64,);}
	</style>
</head>
<body>
	<div id="bomber-logo"></div>
	<p>The following results were detected by <code>{{.Meta.Generator}} {{.Meta.Version}}</code> on {{.Meta.Date}} using the {{.Meta.Provider}} provider.</p>
	{{ if ne (len .Packages) 0 }} 
	<p>
		Vulnerabilities displayed may differ from provider to provider. This list may not contain all possible vulnerabilities. Please try the other providers that <code>bomber</code> supports (osv, ossindex, snyk). There is no guarantee that 
		the next time you scan for vulnerabilities that there won't be more, or less of them. Threats are continuous.
	</p>
	<p>
		EPSS Percentage indicates the % chance that the vulnerability will be exploited. This
		value will assist in prioritizing remediation. For more information on EPSS, refer to
		<a href="https://www.first.org/epss/">https://www.first.org/epss/"</a>.</p>
	</p>
	{{ else }}
	<p>
		No vulnerabilities found!
	</p>
	{{ end }}
	{{ if ne (len .Files) 0 }} 
		<h1>Scanned Files</h1>
		{{ range .Files }}
			<p><b>{{ .Name }}</b> (sha256:{{ .SHA256 }})</p>
		{{ end }}
	{{end}}
	{{ if ne (len .Licenses) 0 }} 
		<h1>Licenses</h1>
		<p>The following licenses were found by <code>bomber</code>:</p>
		<ul>
		{{ range $license := .Licenses }}
			<li>{{ $license }}</li>
		{{ end }}
		</ul>
	{{ else }}
		<p>No license information detected.</b>
	{{ end }}
	{{ if ne (len .Packages) 0 }} 
		<h1>Vulnerability Summary</h1>
		{{ if ne (len .Meta.SeverityFilter) 0 }}
			<p>Only showing vulnerabilities with a severity of <i><b>{{ .Meta.SeverityFilter }}</b></i> or higher.</p>
		{{ end }}
		<table id="summary">
			{{if gt .Summary.Critical 0}}
			<tr><td>Critical:</td><td>{{ .Summary.Critical }}</td></tr>
			{{ end }}
			{{if gt .Summary.High 0}}
			<tr><td>High:</td><td>{{ .Summary.High }}</td></tr>
			{{ end }}
			{{if gt .Summary.Moderate 0}}
			<tr><td>Moderate:</td><td>{{ .Summary.Moderate }}</td></tr>
			{{ end }}
			{{if gt .Summary.Low 0}}
			<tr><td>Low:</td><td>{{ .Summary.Low }}</td></tr>
			{{ end }}
			{{if gt .Summary.Unspecified 0}}
			<tr><td>Unspecified:</td><td>{{ .Summary.Unspecified }}</td></tr>
			{{ end }}
		</table>
		<h1>Vulnerability Details</h1>
		{{ range .Packages }}
			<h2>{{ .Purl }}</h2>
			<p>{{ .Description }}</p>
			<h3>Vulnerabilities</h3>
			{{ range .Vulnerabilities }}
				<div id="vuln">
					{{ if .Title }}
					<h3>{{ .Title }}</h3>
					{{ end }}
					<p>Severity: <span id="{{ .Severity }}">{{ .Severity }}</span></p>
					{{ if ne (len .Epss.Percentile) 0 }} 
						<p>EPSS: <span>{{ .Epss.Percentile }}</span></p>
					{{ end }}
					<p><a href="{{ .Reference }}">Reference Documentation</a></p>
					<p>{{ .Description }}
				</div>
			{{ end }}
			<br/>
		{{ end }}
	{{ end }}
	<div id="footer"></div>
	Powered by the <a href="https://github.com/devops-kung-fu"/>DevOps Kung Fu Mafia</a>
</body>
</html>

`
	return template.Must(template.New(output).Parse(content))
}
